{"changed":true,"filter":false,"title":"resources_controller.rb","tooltip":"/app/controllers/resources_controller.rb","value":"class ResourcesController < ApplicationController\n  before_action :set_resource, only: [:show, :edit, :update, :destroy]\n\n  # GET /resources\n  # GET /resources.json\n  def index\n    # This call has been obviated by making use of the 'routable' flag in the Archetype model\n    # However, we never want a full listing of all Resources in the database\n    @resources = Resource.only('ConceptScheme')\n  end\n\n  # GET /resources/1\n  # GET /resources/1.json\n  def show\n  end\n\n  # GET /resources/new\n  def new\n    @resource = Resource.new\n  end\n\n  # GET /resources/1/edit\n  def edit\n  end\n\n  # POST /resources\n  # POST /resources.json\n  def create\n    @resource = Resource.new(resource_params)\n\n    respond_to do |format|\n      if @resource.save\n        format.html { redirect_to @resource, notice: 'Resource was successfully created.' }\n        format.json { render :show, status: :created, location: @resource }\n      else\n        format.html { render :new }\n        format.json { render json: @resource.errors, status: :unprocessable_entity }\n      end\n    end\n  end\n\n  # PATCH/PUT /resources/1\n  # PATCH/PUT /resources/1.json\n  def update\n    respond_to do |format|\n      if @resource.update(resource_params)\n        format.html { redirect_to @resource, notice: 'Resource was successfully updated.' }\n        format.json { render :show, status: :ok, location: @resource }\n      else\n        format.html { render :edit }\n        format.json { render json: @resource.errors, status: :unprocessable_entity }\n      end\n    end\n  end\n\n  # DELETE /resources/1\n  # DELETE /resources/1.json\n  def destroy\n    @resource.destroy\n    respond_to do |format|\n      format.html { redirect_to resources_url, notice: 'Resource was successfully destroyed.' }\n      format.json { head :no_content }\n    end\n  end\n  \n  def by_label\n    label = Resource.find_by literal: params[:literal].upcase\n    @resource = Resource.find(label.parents.first.subject_id)\n    if @resource\n      I18n.locale = label.language.name\n      render \"show\"\n    end\n  end\n\n  private\n    # Use callbacks to share common setup or constraints between actions.\n    def set_resource\n      @resource = Resource.find_by literal: params[:literal]\n    end\n    # Never trust parameters from the scary internet, only allow the white list through.\n    def resource_params\n      params[:resource]\n    end\nend\n","undoManager":{"mark":-153,"position":100,"stack":[[{"group":"doc","deltas":[{"start":{"row":7,"column":37},"end":{"row":7,"column":38},"action":"insert","lines":["i"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":38},"end":{"row":7,"column":39},"action":"insert","lines":["s"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":39},"end":{"row":7,"column":40},"action":"insert","lines":["t"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":40},"end":{"row":7,"column":41},"action":"insert","lines":["i"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":41},"end":{"row":7,"column":42},"action":"insert","lines":["n"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":42},"end":{"row":7,"column":43},"action":"insert","lines":["g"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":43},"end":{"row":7,"column":44},"action":"insert","lines":[" "]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":44},"end":{"row":7,"column":45},"action":"insert","lines":["o"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":45},"end":{"row":7,"column":46},"action":"insert","lines":["f"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":46},"end":{"row":7,"column":47},"action":"insert","lines":[" "]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":47},"end":{"row":7,"column":48},"action":"insert","lines":["a"]}]}],[{"group":"doc","deltas":[{"start":{"row":7,"column":48}